const fs = require('fs');
const { exec } = require('child_process');

/**
 * A method to verify that the user has given us a slack webhook url to post to
 */
const createFile = (version) => {
    const filename = '.version.js';
    const content = `export const version = '${version}'`;
    fs.writeFileSync(filename, content);

}

const build = () => {
    exec('yarn build', (err, stdout, stderr) => {
        if (err) {
            console.error('build package error: ', err);
            return;
        }

        console.log(`stdout: ${stdout}`);
        console.log(`stderr: ${stderr}`);
    });
}

module.exports = async (context) => {
    const version = context?.nextRelease?.version;
    if (!version) {
        throw new Error(
            'Could not determine the version from semantic release.',
        )
    }
    createFile(version);
    build();
};
